        //DOORS
        //Create Doors (universal)
        function createDoor(groupId, 
            groupName, 
            doorHeight, 
            doorWidth, 
            doorName, 
            groupPositionX, 
            groupPositionY, 
            handleName,
            handlePosTop, 
            handlePosSides) {

            self.scene.remove(groupName);

            groupName = new THREE.Group();
            groupName.name = groupId;
            groupName.add(self.createDoorProto(doorHeight, doorWidth, doorName));
            groupName.position.x = groupPositionX
            groupName.position.y = groupPositionY
            groupName.position.z = -self.options.room.width / 2 + self.options.shelf.width - (self.options.shelf.thickness)
            groupName.getObjectByName(doorName).material.map = self.textures[self.options.doors.texture].map;
            self.loadAndPlaceHandle(handleName, handlePosTop,handlePosSides, self.options.shelf.width + 5, groupName);
            self.scene.add(groupName);
                console.log(self.options.shelf.width)
        };


        function createDoubleDoor(groupId, groupName, doorHeight_1, doorWidth_1, doorName_1, groupPositionX, groupPositionY, handleName_1, handlePosTop_1, handlePosSides_1, doorHeight_2, doorWidth_2, doorName_2, handleName_2, handlePosTop_2, handlePosSides_2) {

            self.scene.remove(groupName);

            groupName = new THREE.Group();
            groupName.name = groupId;

            // 0.15 is the gap between doors
            groupName.add(self.createDoorProto(doorHeight_1, doorWidth_1 / 2 - 0.20, doorName_1));
            groupName.add(self.createDoorProto(doorHeight_2, doorWidth_2 / 2 - 0.20, doorName_2));

            self.loadAndPlaceHandle(handleName_1, handlePosTop_1,handlePosSides_1, 1.5, groupName);
            self.loadAndPlaceHandle(handleName_2, handlePosTop_2, handlePosSides_2, 1.5, groupName);

            groupName.position.x = groupPositionX
            groupName.position.y = groupPositionY
            groupName.position.z = -self.options.room.width / 2 + self.options.shelf.width - (self.options.shelf.thickness)

            groupName.getObjectByName(doorName_1).material.map = self.textures[self.options.doors.texture].map;
            groupName.getObjectByName(doorName_2).material.map = self.textures[self.options.doors.texture].map;

            groupName.getObjectByName(doorName_2).position.x = doorWidth_1 / 2
            // groupName.getObjectByName(handleName_2).position.x = doorWidth_1 / 2

            self.scene.add(groupName);

        };


